-- table with geo info
CREATE TABLE IF NOT EXISTS `db_geo_locations` (
  `ip` int(10) unsigned NOT NULL,
  `latlng` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `zipcode` varchar(10) COLLATE utf8_unicode_ci DEFAULT NULL,
  `city` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `country_code` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `country_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `latitude` float(10,6) NOT NULL,
  `longitude` float(10,6) NOT NULL,
  CONSTRAINT ip_pk PRIMARY KEY (ip)
);

CREATE TABLE IF NOT EXISTS `db_geo_includes` (
  `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `function` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `parameters` varchar(255) COLLATE utf8_unicode_ci NOT NULL
);


-- table with country codes
CREATE TABLE IF NOT EXISTS `db_country_codes` (
  `Name` varchar(44) DEFAULT NULL,
  `Code` varchar(2) DEFAULT NULL
);


CREATE TABLE IF NOT EXISTS `db_time_offset` (
  `latitude` double NOT NULL,
  `longitude` double NOT NULL,
  `offset` decimal(10,1) NOT NULL
);




